import React, { useState } from 'react';
import { FaFileUpload, FaFileExcel } from 'react-icons/fa';
import './styles/App.css';

function App() {
  const [pdfData, setPdfData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedDept, setSelectedDept] = useState(null);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // Reset states
    setError(null);
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.error || 'Upload failed');
      }

      if (!data.departmentData) {
        throw new Error('Invalid data received from server');
      }

      setPdfData(data);
      setError(null);
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Error uploading file');
      setPdfData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleExcelDownload = async () => {
    try {
      setLoading(true);
      
      // Make the API call
      const response = await fetch('http://localhost:5000/generate-excel', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(pdfData)
      });

      if (!response.ok) {
        throw new Error('Failed to generate Excel file');
      }

      // Get the blob from response
      const blob = await response.blob();
      
      // Create download link
      const downloadUrl = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = downloadUrl;
      
      // Set filename from Content-Disposition header or use default
      const contentDisposition = response.headers.get('Content-Disposition');
      const fileName = contentDisposition
        ? contentDisposition.split('filename=')[1].replace(/"/g, '')
        : `results_${pdfData.semester}_${new Date().toISOString().split('T')[0]}.xlsx`;
      
      link.download = fileName;
      
      // Append to document, click, and cleanup
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(downloadUrl);
      
      setLoading(false);
    } catch (error) {
      console.error('Error downloading Excel:', error);
      setError('Failed to download Excel file');
      setLoading(false);
    }
  };

  const renderDepartmentAnalysis = (dept, students) => {
    const regularStudents = students.filter(s => 
      s.registerNo.match(/(?:[A-Z]+)?([0-9]{2})[A-Z]{2}[0-9]{3}/)?.[1] === pdfData.batch.toString()
    );
    const supplementaryStudents = students.filter(s => 
      s.registerNo.match(/(?:[A-Z]+)?([0-9]{2})[A-Z]{2}[0-9]{3}/)?.[1] !== pdfData.batch.toString()
    );

    return (
      <div className="dept-analysis">
        <h2>{dept} Analysis</h2>
        
        <div className="analysis-grid">
          <div className="analysis-card">
            <h3>Overview</h3>
            <div className="stat-row">
              <div className="stat-item">
                <label>Regular Students:</label>
                <span>{regularStudents.length}</span>
              </div>
              <div className="stat-item">
                <label>Supplementary Students:</label>
                <span>{supplementaryStudents.length}</span>
              </div>
              <div className="stat-item">
                <label>Pass Percentage (Regular):</label>
                <span>
                  {((regularStudents.filter(s => !s.FailedSubjects).length / regularStudents.length) * 100).toFixed(2)}%
                </span>
              </div>
            </div>
          </div>

          <div className="analysis-card">
            <h3>Toppers (SGPA ≥ 9.0)</h3>
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>Register No</th>
                    <th>SGPA</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {students
                    .filter(s => s.SGPA >= 9.0)
                    .sort((a, b) => b.SGPA - a.SGPA)
                    .map(s => (
                      <tr key={s.registerNo}>
                        <td>{s.registerNo}</td>
                        <td>{s.SGPA}</td>
                        <td>{regularStudents.includes(s) ? 'Regular' : 'Supplementary'}</td>
                      </tr>
                    ))
                  }
                </tbody>
              </table>
            </div>
          </div>

          <div className="analysis-card">
            <h3>Failed Students (Regular Batch)</h3>
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>Register No</th>
                    <th>Failed Subjects</th>
                    <th>SGPA</th>
                  </tr>
                </thead>
                <tbody>
                  {regularStudents
                    .filter(s => s.FailedSubjects)
                    .map(s => (
                      <tr key={s.registerNo}>
                        <td>{s.registerNo}</td>
                        <td>{s.FailedSubjects}</td>
                        <td>{s.SGPA}</td>
                      </tr>
                    ))
                  }
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="app">
      {!pdfData ? (
        <div className="upload-container">
          <h1>Result Analysis System</h1>
          <div className="upload-box">
            <input
              type="file"
              accept=".pdf"
              onChange={handleFileUpload}
              id="file-upload"
              disabled={loading}
            />
            <label htmlFor="file-upload" className="upload-label">
              <FaFileUpload />
              <span>Choose PDF file</span>
            </label>
            {loading && <div className="loader">Processing...</div>}
            {error && <div className="error-message">{error}</div>}
          </div>
        </div>
      ) : (
        <div className="dashboard">
          <div className="dashboard-header">
            <h1>Results Analysis - {pdfData.semester}</h1>
            <button 
              className={`excel-btn ${loading ? 'loading' : ''}`}
              onClick={handleExcelDownload}
              disabled={loading}
            >
              <FaFileExcel />
              {loading ? 'Generating...' : 'Generate Excel'}
            </button>
            {error && <div className="error-message">{error}</div>}
          </div>
          
          <div className="dashboard-content">
            <div className="departments-list">
              <h2>Departments</h2>
              {Object.entries(pdfData.departmentData).map(([dept, students]) => (
                <div
                  key={dept}
                  className={`dept-item ${selectedDept === dept ? 'selected' : ''}`}
                  onClick={() => setSelectedDept(dept)}
                >
                  <h3>{dept}</h3>
                  <p>{students.length} Students</p>
                </div>
              ))}
            </div>
            
            <div className="analysis-content">
              {selectedDept ? (
                renderDepartmentAnalysis(selectedDept, pdfData.departmentData[selectedDept])
              ) : (
                <div className="select-prompt">
                  ← Select a department to view analysis
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
